import React, { useEffect, useState } from 'react';
import { fetchAvailability } from '../api';

export default function CalendarGrid({ onSelectSlot }){
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(()=>{
    setLoading(true);
    fetchAvailability(null, 10).then(setData).catch(()=>setData([])).finally(()=>setLoading(false));
  },[]);

  if(loading) return <p>Cargando calendario...</p>;

  return (
    <div className="days-grid">
      {data.map(day => (
        <div className="day-card" key={day.date}>
          <div className="day-header">{day.date}</div>
          <div className="slots">
            {day.slots.map(s => (
              <button key={s.time} disabled={!s.available}
                className={`slot-btn ${s.available? 'available':'taken'}`}
                onClick={()=> onSelectSlot(day.date, s.time, s.available)}>
                {s.time}
              </button>
            ))}
          </div>
        </div>
      ))}
    </div>
  );
}
