import React, { useState } from 'react';
import { adminListBookings, adminDeleteBooking } from '../api';

// Simple admin UI that asks for user:pass and lists bookings.
export default function Admin(){
  const [user,setUser] = useState('');
  const [passw,setPassw] = useState('');
  const [auth, setAuth] = useState(null);
  const [bookings, setBookings] = useState([]);

  async function login(){
    const token = 'Basic ' + btoa(user + ':' + passw);
    try{
      const res = await adminListBookings(token);
      setBookings(res);
      setAuth(token);
    }catch(e){
      alert('Login falló o no autorizado');
    }
  }

  async function refresh(){
    try{
      const res = await adminListBookings(auth);
      setBookings(res);
    }catch(e){ alert('Error'); }
  }

  async function remove(id){
    if(!confirm('Eliminar reserva ' + id + '?')) return;
    try{
      await adminDeleteBooking(id, auth);
      setBookings(b => b.filter(x => x.id !== id));
    }catch(e){ alert('Error eliminando'); }
  }

  return (
    <div className="admin-box">
      <h3>Admin (simple)</h3>
      {!auth ? (
        <div className="admin-login">
          <input placeholder="user" value={user} onChange={e=>setUser(e.target.value)} />
          <input placeholder="pass" type="password" value={passw} onChange={e=>setPassw(e.target.value)} />
          <button onClick={login}>Login</button>
        </div>
      ) : (
        <div>
          <button onClick={refresh}>Refrescar reservas</button>
          <div>
            {bookings.length === 0 ? <p>No hay reservas</p> : bookings.map(b => (
              <div key={b.id} style={{borderBottom:'1px solid #eee', padding:'8px 0'}}>
                <strong>{b.nombre}</strong> — {b.servicio} — {b.fecha} {b.hora}
                <button style={{marginLeft:8}} onClick={()=>remove(b.id)}>Eliminar</button>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}
