import React, { useState } from 'react';
import CalendarGrid from './components/CalendarGrid';
import BookingModal from './components/BookingModal';
import Works from './components/Works';
import Footer from './components/Footer';
import Admin from './components/Admin';

export default function App(){
  const [modal,setModal] = useState({open:false, date:null, time:null});
  const [lastBooking,setLastBooking] = useState(null);

  function onSelectSlot(date,time,available){
    if(!available) return alert('Slot no disponible');
    setModal({open:true, date, time});
  }
  function onClose(){ setModal({open:false, date:null, time:null}); }
  function onSuccess(b){ setLastBooking(b); alert('Reserva confirmada: ' + b.id); }

  return (
    <div className="container">
      <header className="top"><h1>Uñas by [Nombre]</h1><p>Reservá tu turno online</p></header>
      <main>
        <CalendarGrid onSelectSlot={onSelectSlot} />
        <Works />
        {lastBooking && (
          <div className="mensaje">Reserva confirmada: {lastBooking.fecha} {lastBooking.hora} · {lastBooking.servicio}</div>
        )}
      </main>

      <BookingModal open={modal.open} onClose={onClose} date={modal.date} time={modal.time} onSuccess={onSuccess} />
      <Admin />
      <Footer />
    </div>
  );
}
