import React, { useState } from 'react';
import { createBooking } from '../api';

export default function BookingModal({ open, onClose, date, time, onSuccess }){
  const [nombre,setNombre] = useState('');
  const [telefono,setTelefono] = useState('');
  const [email,setEmail] = useState('');
  const [servicio,setServicio] = useState('Manicura Simple');
  const [loading,setLoading] = useState(false);
  const [error,setError] = useState(null);

  if(!open) return null;

  async function submit(){
    setLoading(true); setError(null);
    try{
      const payload = { nombre, telefono, email, servicio, fecha: date, hora: time, deposit_paid: true };
      const res = await createBooking(payload);
      onSuccess(res.booking);
      onClose();
    }catch(e){ setError(e.message); }
    finally{ setLoading(false); }
  }

  return (
    <div className="modal-backdrop">
      <div className="modal">
        <h3>Reservar {date} - {time}</h3>
        {error && <div className="error">{error}</div>}
        <label>Nombre</label>
        <input value={nombre} onChange={e=>setNombre(e.target.value)} />
        <label>Teléfono</label>
        <input value={telefono} onChange={e=>setTelefono(e.target.value)} />
        <label>Email</label>
        <input value={email} onChange={e=>setEmail(e.target.value)} />
        <label>Servicio</label>
        <input value={servicio} onChange={e=>setServicio(e.target.value)} />

        <p>Seña requerida: <strong>$2000</strong> (simulada)</p>

        <div style={{display:'flex', gap:8, justifyContent:'flex-end'}}>
          <button onClick={onClose} disabled={loading}>Cancelar</button>
          <button onClick={submit} disabled={loading}>Pagar seña y confirmar</button>
        </div>
      </div>
    </div>
  );
}
