import React, { useEffect, useState } from 'react';
import { fetchWorks } from '../api';

export default function Works(){
  const [items,setItems] = useState([]);
  useEffect(()=>{ fetchWorks().then(setItems).catch(()=>setItems([])); },[]);
  return (
    <section>
      <h2>Trabajos realizados</h2>
      <div className="works-grid">
        {items.map(w=> (
          <div className="work-card" key={w.id}>
            <div className="work-thumb">{w.image ? <img src={w.image} alt={w.title} /> : <div className="placeholder">No image</div>}</div>
            <h4>{w.title}</h4>
            <p>{w.description}</p>
          </div>
        ))}
      </div>
    </section>
  );
}
